package LoopNcontrol;

public class Prog3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int j=0; j<=6; j++)
		{
	           if (j==4)
	           {
	        	   continue;
	           }
	           else if(j==5)
	           {
	        	   break;
	           }
	        System.out.println(j+" ");
		}

	}

}
